import React,{UseState} from 'react'
import logo from './logo.svg';
import './App.css';

function Button(props){
  return(
    <button>{props.increment}</button>
  );
}


function Display(props){
  return(
    <div>{props.value}</div>
  );
}

function App() {

  const [value,setValue]=UseState(0);
  return (
    <div>
      <Button increment="10"/>
      <Display value={value}/>
    </div>

  );
}

export default App;
